                fieldValue.Add("col_f9", record.Пакування.UnigueID.UGuid);
                fieldValue.Add("col_g2", record.Валюта.UnigueID.UGuid);
                
                record.UID = base.BaseSave(record.UID, period, owner, fieldValue);
            }
            base.BaseCommitTransaction();
        }
        
