using Gtk;
using InterfaceGtk;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade
{
    class Номенклатура_Елемент : ДовідникЕлемент
    {
        public Номенклатура_Objest Елемент { get; init; } = new Номенклатура_Objest();

        #region Fields
        Entry Назва = new Entry() { WidthRequest = 500 };
                        Entry Код = new Entry() { WidthRequest = 100 };
                        Entry НазваПовна = new Entry() { WidthRequest = 500 };
                        TextView Опис = new TextView() { WrapMode = WrapMode.Word };
                        Entry Артикул = new Entry() { WidthRequest = 500 };
                        ComboBoxText ТипНоменклатури = new ComboBoxText();
                Виробники_PointerControl Виробник = new Виробники_PointerControl() { Caption = "Виробник", WidthPresentation = 500 };
                ВидиНоменклатури_PointerControl ВидНоменклатури = new ВидиНоменклатури_PointerControl() { Caption = "ВидНоменклатури", WidthPresentation = 500 };
                ПакуванняОдиниціВиміру_PointerControl ОдиницяВиміру = new ПакуванняОдиниціВиміру_PointerControl() { Caption = "ОдиницяВиміру", WidthPresentation = 500 };
                Номенклатура_Папки_PointerControl Папка = new Номенклатура_Папки_PointerControl() { Caption = "Папка", WidthPresentation = 500 };
                Файли_PointerControl ОсновнаКартинкаФайл = new Файли_PointerControl() { Caption = "ОсновнаКартинкаФайл", WidthPresentation = 500 };

        #endregion

        #region TabularParts

            // Таблична частина "Файли"
            Номенклатура_ТабличнаЧастина_Файли Файли = new Номенклатура_ТабличнаЧастина_Файли() { WidthRequest = 500, HeightRequest = 300 };

        #endregion

        public Номенклатура_Елемент() : base() 
        { 
            Елемент.UnigueIDChanged += UnigueIDChanged;
            Елемент.CaptionChanged += CaptionChanged;


                foreach (var field in ПсевдонімиПерелічення.ТипиНоменклатури_List())
                    ТипНоменклатури.Append(field.Value.ToString(), field.Name);

        }

        protected override void CreatePack1(Box vBox)
        {

                // Назва
                CreateField(vBox, "Назва:", Назва);

                // Код
                CreateField(vBox, "Код:", Код);

                // НазваПовна
                CreateField(vBox, "НазваПовна:", НазваПовна);

                // Опис
                CreateFieldView(vBox, "Опис:", Опис, 500, 200);

                // Артикул
                CreateField(vBox, "Артикул:", Артикул);

                // ТипНоменклатури
                CreateField(vBox, "ТипНоменклатури:", ТипНоменклатури);

                // Виробник
                CreateField(vBox, null, Виробник);

                // ВидНоменклатури
                CreateField(vBox, null, ВидНоменклатури);

                // ОдиницяВиміру
                CreateField(vBox, null, ОдиницяВиміру);

                // Папка
                CreateField(vBox, null, Папка);

                // ОсновнаКартинкаФайл
                CreateField(vBox, null, ОсновнаКартинкаФайл);

        }

        protected override void CreatePack2(Box vBox)
        {

                // Таблична частина "Файли" 
                CreateTablePart(vBox, "Файли:", Файли);

        }

        #region Присвоєння / зчитування значень

        public override async void SetValue()
        {
            Назва.Text = Елемент.Назва;
                            Код.Text = Елемент.Код;
                            НазваПовна.Text = Елемент.НазваПовна;
                            Опис.Buffer.Text = Елемент.Опис;
                            Артикул.Text = Елемент.Артикул;
                            ТипНоменклатури.ActiveId = Елемент.ТипНоменклатури.ToString(); if (ТипНоменклатури.Active == -1) ТипНоменклатури.Active = 0;
                    Виробник.Pointer = Елемент.Виробник;
                    ВидНоменклатури.Pointer = Елемент.ВидНоменклатури;
                    ОдиницяВиміру.Pointer = Елемент.ОдиницяВиміру;
                    Папка.Pointer = Елемент.Папка;
                    ОсновнаКартинкаФайл.Pointer = Елемент.ОсновнаКартинкаФайл;

                // Таблична частина "Файли"
                Файли.ЕлементВласник = Елемент;
                await Файли.LoadRecords();

        }

        protected override void GetValue()
        {
            Елемент.Назва = Назва.Text;
                            Елемент.Код = Код.Text;
                            Елемент.НазваПовна = НазваПовна.Text;
                            Елемент.Опис = Опис.Buffer.Text;
                            Елемент.Артикул = Артикул.Text;
                            if (ТипНоменклатури.Active != -1) Елемент.ТипНоменклатури = Enum.Parse<ТипиНоменклатури>(ТипНоменклатури.ActiveId);
                    Елемент.Виробник = Виробник.Pointer;
                    Елемент.ВидНоменклатури = ВидНоменклатури.Pointer;
                    Елемент.ОдиницяВиміру = ОдиницяВиміру.Pointer;
                    Елемент.Папка = Папка.Pointer;
                    Елемент.ОсновнаКартинкаФайл = ОсновнаКартинкаФайл.Pointer;

        }

        #endregion

        protected override async ValueTask<bool> Save()
        {
            bool isSaved = false;
            try
            {
                if (await Елемент.Save())
                {
                    await Файли.SaveRecords(); // Таблична частина "Файли"

                    isSaved = true;
                }
            }
            catch (Exception ex)
            {
                ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
            }
            return isSaved;
        }
    }
}

/*     
        Номенклатура_MultiplePointerControl.cs
        MultiplePointerControl
*/

using Gtk;
using InterfaceGtk;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade
{
    public class Номенклатура_MultiplePointerControl : MultiplePointerControl
    {
        event EventHandler<Номенклатура_Pointer> PointerChanged;

        public Номенклатура_MultiplePointerControl()
        {
            pointer = new Номенклатура_Pointer();
            WidthPresentation = 300;
            Caption = $"{Номенклатура_Const.FULLNAME}:";
            PointerChanged += async (object? _, Номенклатура_Pointer pointer) =>
            {
                Presentation = pointer != null ? await pointer.GetPresentation() : "";
                if (pointers.Count > 1) Presentation += $" ... {pointers.Count}";
            };
        }

        Номенклатура_Pointer pointer;
        List<Номенклатура_Pointer> pointers = [];
        public Номенклатура_Pointer Pointer
        {
            get
            {
                return pointer;
            }
            set
            {
                pointer = value;
                PointerChanged?.Invoke(null, pointer);
            }
        }

        public Номенклатура_Pointer[] GetPointers()
        {
            Номенклатура_Pointer[] copy = new Номенклатура_Pointer[pointers.Count];
            pointers.CopyTo(copy);

            return copy;
        }

        void Add(Номенклатура_Pointer item)
        {
            if (!pointers.Exists((Номенклатура_Pointer x) => x.UnigueID.ToString() == item.UnigueID.ToString()))
                pointers.Add(item);

            Pointer = item;
            //AfterSelectFunc?.Invoke();
        }



        protected override async void OpenSelect(object? sender, EventArgs args)
        {
            Popover popover = new Popover((Button)sender!) { Position = PositionType.Bottom, BorderWidth = 2 };
            BeforeClickOpenFunc?.Invoke();
            Номенклатура_ШвидкийВибір page = new Номенклатура_ШвидкийВибір
            {
                PopoverParent = popover,
                DirectoryPointerItem = pointer.UnigueID,
                CallBack_OnSelectPointer = (UnigueID selectPointer) =>
                {
                    Add(new Номенклатура_Pointer(selectPointer));
                },
                CallBack_OnMultipleSelectPointer = (UnigueID[] selectPointers) =>
                {
                    foreach (var selectPointer in selectPointers)
                        Add(new Номенклатура_Pointer(selectPointer));
                }
            };

            popover.Add(page);
            popover.ShowAll();

            await page.SetValue();
        }

        protected override async ValueTask FillList(ListBox listBox)
        {
            foreach (Номенклатура_Pointer item in pointers)
            {
                Box hBox = new Box(Orientation.Horizontal, 0);
                ListBoxRow listBoxRow = [hBox];

                LinkButton linkName = new LinkButton("", await item.GetPresentation()) { Halign = Align.Start, Image = new Image(InterfaceGtk.Іконки.ДляКнопок.Doc), AlwaysShowImage = true };
                linkName.Clicked += (object? sender, EventArgs args) =>
                {
                    if (Pointer.UnigueID.ToString() != item.UnigueID.ToString())
                        Pointer = item;
                };

                hBox.PackStart(linkName, true, true, 0);

                //Remove
                LinkButton linkRemove = new LinkButton("") { Halign = Align.Start, Image = new Image(InterfaceGtk.Іконки.ДляКнопок.Clean), AlwaysShowImage = true };
                linkRemove.Clicked += (object? sender, EventArgs args) =>
                {
                    pointers.Remove(item);
                    listBox.Remove(listBoxRow);

                    if (Pointer.UnigueID.ToString() == item.UnigueID.ToString())
                        Pointer = pointers.Count > 0 ? pointers[0] : new Номенклатура_Pointer();
                    else
                        PointerChanged?.Invoke(null, pointer);
                };

                hBox.PackEnd(linkRemove, false, false, 0);

                listBox.Add(listBoxRow);
                listBox.ShowAll();
            }
        }

        protected override void OnClear(object? sender, EventArgs args)
        {
            pointers = [];
            Pointer = new Номенклатура_Pointer();
            AfterSelectFunc?.Invoke();
            AfterClearFunc?.Invoke();
        }
    }
}

