using InterfaceGtk;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade
{
    static class КасиККМ_Функції
    {
        public static List<Where> Відбори(string searchText)
        {
            return
            [

                //Назва
                new Where(КасиККМ_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

            ];
        }

        public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
            Action<UnigueID?>? сallBack_LoadRecords = null, 
            Action<UnigueID>? сallBack_OnSelectPointer = null)
        {
            КасиККМ_Елемент page = new КасиККМ_Елемент
            {
                IsNew = IsNew,
                CallBack_LoadRecords = сallBack_LoadRecords,
                CallBack_OnSelectPointer = сallBack_OnSelectPointer
            };

            if (IsNew)
            {
                await page.Елемент.New();

            }
            else if (unigueID == null || !await page.Елемент.Read(unigueID))
            {
                Message.Error(Program.GeneralForm, "Не вдалось прочитати!");
                return;
            }

            NotebookFunction.CreateNotebookPage(Program.GeneralNotebook, page.Caption, () => page);
            await NotebookFunction.AddLockObjectFunc(Program.GeneralNotebook, page.Name, page.Елемент);
            await page.LockInfo(page.Елемент);
            page.SetValue();
        }

        public static async ValueTask SetDeletionLabel(UnigueID unigueID)
        {
            КасиККМ_Pointer Вказівник = new(unigueID);
            bool? label = await Вказівник.GetDeletionLabel();
            if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
        }

        public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
        {
            КасиККМ_Objest Обєкт = new КасиККМ_Objest();
            if (await Обєкт.Read(unigueID))
            {
                КасиККМ_Objest Новий = await Обєкт.Copy(true);
                await Новий.Save();

                return Новий.UnigueID;
            }
            else
            {
                Message.Error(Program.GeneralForm, "Не вдалось прочитати!");
                return null;
            }
        }
    }
}

/*
        КасиККМ_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade
{
    class КасиККМ_Елемент : ДовідникЕлемент
    {
        public КасиККМ_Objest Елемент { get; init; } = new КасиККМ_Objest();

        #region Fields
        Entry Назва = new Entry() { WidthRequest = 500 };
                        Склади_PointerControl Склад = new Склади_PointerControl() { Caption = "Склад", WidthPresentation = 500 };
                ComboBoxText Тип = new ComboBoxText();
                Валюти_PointerControl Валюта = new Валюти_PointerControl() { Caption = "Валюта", WidthPresentation = 500 };

        #endregion

        #region TabularParts

        #endregion

        public КасиККМ_Елемент() : base() 
        { 
            Елемент.UnigueIDChanged += UnigueIDChanged;
            Елемент.CaptionChanged += CaptionChanged;


                foreach (var field in ПсевдонімиПерелічення.ТипККМ_List())
                    Тип.Append(field.Value.ToString(), field.Name);

        }

        protected override void CreatePack1(Box vBox)
        {

                // Назва
                CreateField(vBox, "Назва:", Назва);

                // Склад
                CreateField(vBox, null, Склад);

                // Тип
                CreateField(vBox, "Тип:", Тип);

                // Валюта
                CreateField(vBox, null, Валюта);

        }

        protected override void CreatePack2(Box vBox)
        {

        }

        #region Присвоєння / зчитування значень

        public override  void SetValue()
        {
            Назва.Text = Елемент.Назва;
                            Склад.Pointer = Елемент.Склад;
                    Тип.ActiveId = Елемент.Тип.ToString(); //if (Тип.Active == -1) Тип.Active = 0;
                    Валюта.Pointer = Елемент.Валюта;

        }

        protected override void GetValue()
        {
            Елемент.Назва = Назва.Text;
                            Елемент.Склад = Склад.Pointer;
                    Елемент.Тип = ПсевдонімиПерелічення.ТипККМ_FindByName(Тип.ActiveId);
                    Елемент.Валюта = Валюта.Pointer;

        }

        #endregion

        protected override async ValueTask<bool> Save()
        {
            bool isSaved = false;
            try
            {
                if (await Елемент.Save())
                {

                    isSaved = true;
                }
            }
            catch (Exception ex)
            {
                ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
            }
            return isSaved;
        }
    }
}

/*     
        КасиККМ.cs
        Список
*/

using Gtk;
using InterfaceGtk;
using AccountingSoftware;

using GeneratedCode;
using GeneratedCode.Довідники;
using ТабличніСписки = GeneratedCode.Довідники.ТабличніСписки;

namespace StorageAndTrade
{
    public class КасиККМ : ДовідникЖурнал
    {


        public КасиККМ() : base()
        {
            ТабличніСписки.КасиККМ_Записи.AddColumns(TreeViewGrid);

        }

        #region Override

        public override async ValueTask LoadRecords()
        {
            ТабличніСписки.КасиККМ_Записи.ОчиститиВідбір(TreeViewGrid);

            await ТабличніСписки.КасиККМ_Записи.LoadRecords(TreeViewGrid, OpenFolder, SelectPointerItem, DirectoryPointerItem);
        }

        public override async ValueTask LoadRecords_OnSearch(string searchText)
        {
            ТабличніСписки.КасиККМ_Записи.ОчиститиВідбір(TreeViewGrid);

            //Відбори
            ТабличніСписки.КасиККМ_Записи.ДодатиВідбір(TreeViewGrid, КасиККМ_Функції.Відбори(searchText));

            await ТабличніСписки.КасиККМ_Записи.LoadRecords(TreeViewGrid, OpenFolder);
        }

        public async override ValueTask LoadRecords_OnFilter()
        {
            await ТабличніСписки.КасиККМ_Записи.LoadRecords(TreeViewGrid);
        }

        public async ValueTask UpdateRecords(List<ObjectChanged> recordsChanged)
        {
            await ТабличніСписки.КасиККМ_Записи.UpdateRecords(TreeViewGrid, recordsChanged);
        }

        protected override void FillFilterList(ListFilterControl filterControl)
        {
            ТабличніСписки.КасиККМ_Записи.CreateFilter(TreeViewGrid, filterControl);
        }

        protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
        {
            await КасиККМ_Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, null);
        }

        protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
        {
            await КасиККМ_Функції.SetDeletionLabel(unigueID);
        }

        protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
        {
            return await КасиККМ_Функції.Copy(unigueID);
        }

        protected override async ValueTask VersionsHistory(UnigueID unigueID)
        {
            await СпільніФорми_ІсторіяЗміниДаних_Список.Сформувати(new КасиККМ_Pointer(unigueID).GetBasis());
        }

        protected override async ValueTask BeforeSetValue()
        {
            NotebookFunction.AddChangeFunc(Program.GeneralNotebook, Name, UpdateRecords, КасиККМ_Const.POINTER);
             await BeforeLoadRecords();
        }

        #endregion
    }
}

